import React, { useState, useEffect } from "react";
import ProductInfo from "./ProductInfo"; // Component to display products
import AdminPanel from "./AdminPanel"; // Your Admin Panel (for adding products)
import './ProductPage.css';

function ProductPage1() {
  const [products, setProducts] = useState([]);
  const [isAdmin, setIsAdmin] = useState(false);

  // Fetch products from the backend (MongoDB)
  useEffect(() => {
    fetch("/api/products")
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.error("Error fetching products:", err));
  }, []);

  return (
    <div className="App">
      <h1 className="Product-h1">Our Products</h1>
      <button onClick={() => setIsAdmin(!isAdmin)}>
        {isAdmin ? "Switch to User Mode" : "Switch to Admin Mode"}
      </button>

      {isAdmin ? (
        <AdminPanel />
      ) : (
        <ProductInfo products={products} />
      )}
    </div>
  );
}

export default ProductPage1;
