import React from "react";
import { FaInstagram, FaEnvelope, FaLinkedin, FaTwitter } from "react-icons/fa";
import './AboutPage.css'
import NavBar from "../Navbar";

const WhyChooseUs = () => {
  const features = [
    {
      title: "Comprehensive Solutions",
      description:
        "RcoSource offers comprehensive e-commerce solutions, including account management, advertising optimization, cataloging, logistics, and brand protection.",
      icon: "\uD83D\uDCBC",
    },
    {
      title: "Customized Strategies",
      description:
        "We understand every business is unique, so we take the time to understand your goals, challenges, and audience.",
      icon: "\uD83D\uDCA1",
    },
    {
      title: "Data-Driven Decision-Making",
      description:
        "At RcoSource, we use advanced analytics tools to gather valuable insights about your e-commerce performance.",
      icon: "\uD83D\uDCCA",
    },
    {
      title: "Proven Track Record",
      description:
        "With a strong track record of success, RcoSource has helped numerous clients achieve remarkable results.",
      icon: "\uD83D\uDCDC",
    },
  ];

  return (
    <section className="why-choose-us">
      <NavBar></NavBar>
      <h2>Why Choose Us</h2>
      <div className="features-container">
        {features.map((feature, index) => (
          <div key={index} className="feature">
            <span className="feature-icon">{feature.icon}</span>
            <div>
              <h3 className="feature-title">{feature.title}</h3>
              <p className="feature-description">{feature.description}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

const About = () => {
  return (
    <section className="contact-container">
      <h2 className="contact-heading">Contact Us Today</h2>
      <p className="contact-text">
        Our source invites you to embark on a successful e-commerce journey with us. Feel free to contact us and hire us as your e-commerce partners at our offices in Jaipur and Bangalore.
      </p>
    </section>
  );
};

const Footer = () => {
  return (
    <footer className="footer">
      <div className="footer-container">
        <div className="footer-company">
          <img src="logo.png" alt="company logo" className="footer-logo" />
          <p>We help you in creating an online presence for your business by providing you expertise services.</p>
        </div>
        <div className="footer-address">
          <h3>Company Headquarter</h3>
          <p>102, 18/1 MM Tower, Ushaganj, Chhawni, Indore, Madhya Pradesh, 452001</p>
        </div>
        <div className="footer-contact">
          <h3>Phone</h3>
          <p className="highlight">+91-8962874720</p>
          <h3>Email</h3>
          <p className="highlight">info@rcosource.in</p>
        </div>
      </div>
      <div className="footer-bottom">
        <div className="footer-links">
          <h3>Follow Us</h3>
          <div className="social-icons">
            <FaInstagram />
            <FaEnvelope />
            <FaLinkedin />
            <FaTwitter />
          </div>
        </div>
        <div className="footer-bottom-text">
          <p>Copyright Â© 2025. All Rights Reserved.</p>
        </div>
      </div>
    </footer>
  );
};

const AboutPage = () => {
  return (
    <div>
      <WhyChooseUs />
      <About />
      <Footer />
    </div>
  );
};

export default AboutPage;
