import React, { useState, useEffect } from 'react';
import './Form.css';
import { useNavigate } from 'react-router-dom';

const Form = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [isOtpStep, setIsOtpStep] = useState(false);
  const [otp, setOtp] = useState('');
  const [userNumber, setUserNumber] = useState('');

  const [name, setName] = useState('');
  const [number, setNumber] = useState('');
  const [email, setEmail] = useState('');
  const [location, setLocation] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) navigate('/');
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (isLogin) {
      // LOGIN FLOW
      try {
        const res = await fetch('http://localhost:5000/api/login', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ email, password }),
        });

        const data = await res.json();

        if (res.status === 200) {
          alert('Login successful!');
          if (data.token) localStorage.setItem('token', data.token);
          navigate('/');
        } else {
          alert(data.message || 'Login failed');
        }
      } catch (err) {
        console.error(err);
        alert('Server error');
      }
    } else {
      // REGISTER FLOW
      try {
        const res = await fetch('http://localhost:5000/api/register', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ name, number, email, location, password }),
        });

        const data = await res.json();

        if (res.status === 201) {
          alert('OTP sent to your number');
          setUserNumber(number); // Save number for OTP verification
          setIsOtpStep(true); // Show OTP input
        } else if (res.status === 409) {
          alert('User already exists. Redirecting to login...');
          setIsLogin(true);
        } else {
          alert(data.message || 'Something went wrong');
        }
      } catch (err) {
        console.error(err);
        alert('Server error');
      }
    }
  };

  const handleVerifyOtp = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:5000/api/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ number: userNumber, otp }),
      });

      const data = await res.json();

      if (res.status === 200) {
        alert('OTP verified successfully!');
        if (data.token) localStorage.setItem('token', data.token);
        navigate('/');
      } else {
        alert(data.message || 'OTP verification failed');
      }
    } catch (err) {
      console.error(err);
      alert('Server error');
    }
  };

  return (
    <div className="container">
      <div className="form-box">
        <h1>{isLogin ? 'Login' : isOtpStep ? 'Verify OTP' : 'Sign Up'}</h1>

        <form onSubmit={isOtpStep ? handleVerifyOtp : handleSubmit}>
          {!isLogin && !isOtpStep && (
            <>
              <input
                type="text"
                placeholder="Full Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
              <input
                type="text"
                placeholder="Phone Number"
                value={number}
                onChange={(e) => setNumber(e.target.value)}
                required
              />
              <input
                type="text"
                placeholder="Location"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                required
              />
            </>
          )}

          {!isOtpStep && (
            <>
              <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </>
          )}

          {isOtpStep && (
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              required
            />
          )}

          <button className="btnfrom" type="submit">
            {isOtpStep ? 'Verify OTP' : isLogin ? 'Login' : 'Sign Up'}
          </button>

          {!isOtpStep && (
            <p className="switch-form">
              {isLogin ? "Don't have an account?" : 'Already have an account?'}{' '}
              <span onClick={() => setIsLogin(!isLogin)}>
                {isLogin ? 'Sign up' : 'Login'}
              </span>
            </p>
          )}
        </form>
      </div>
    </div>
  );
};

export default Form;
