import React, { useState, useEffect } from 'react';

const AdminDashboard = () => {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({ name: '', price: '', id: null });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const res = await fetch('http://localhost:5000/api/products');
    const data = await res.json();
    setProducts(data);
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleAdd = async () => {
    const res = await fetch('http://localhost:5000/api/products', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name: form.name, price: form.price }),
    });
    if (res.ok) {
      fetchProducts();
      setForm({ name: '', price: '', id: null });
    }
  };

  const handleUpdate = async () => {
    await fetch(`http://localhost:5000/api/products/${form.id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name: form.name, price: form.price }),
    });
    fetchProducts();
    setForm({ name: '', price: '', id: null });
  };

  const handleEdit = (product) => {
    setForm({ name: product.name, price: product.price, id: product._id });
  };

  const handleDelete = async (id) => {
    await fetch(`http://localhost:5000/api/products/${id}`, { method: 'DELETE' });
    fetchProducts();
  };

  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard</h2>

      <input
        type="text"
        name="name"
        placeholder="Product Name"
        value={form.name}
        onChange={handleChange}
      />
      <input
        type="number"
        name="price"
        placeholder="Price"
        value={form.price}
        onChange={handleChange}
      />
      {form.id ? (
        <button onClick={handleUpdate}>Update Product</button>
      ) : (
        <button onClick={handleAdd}>Add Product</button>
      )}

      <ul>
        {products.map((product) => (
          <li key={product._id}>
            {product.name} - ${product.price}{' '}
            <button onClick={() => handleEdit(product)}>Edit</button>
            <button onClick={() => handleDelete(product._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;
