// src/components/RegistrationForm.js
import React, { useState } from 'react';
import './Form.css'; // Import your custom CSS
import { Link } from 'react-router-dom';

const Form = () => {
  const [name, setName] = useState('');
  const [number, setNumber] = useState('');
  const [email, setEmail] = useState('');
  const [location, setLocation] = useState('');
  const [password, setPassword] = useState('');
  const [isUserRegistered, setIsUserRegistered] = useState(false); // Mock check if user exists

  const handleSubmit = (e) => {
    e.preventDefault();

    // Mock check for existing user (can be replaced with real validation)
    if (isUserRegistered) {
      alert("User is already registered. Redirecting to login.");
      // Implement redirection to the login page
    } else {
      // Here you'd add the logic to register the user (e.g., API call)
      alert('User Registered successfully!');
    }
  };

  return (
    <div className="form-container">
      <h2>Registration Form</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>

        <div>
          <label htmlFor="number">Phone Number:</label>
          <input
            type="tel"
            id="number"
            value={number}
            onChange={(e) => setNumber(e.target.value)}
            required
          />
        </div>

        <div>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        <div>
          <label htmlFor="location">Location:</label>
          <input
            type="text"
            id="location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            required
          />
        </div>

        <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <button type="submit">Register</button>
      </form>

      <small>
        Don't have an account?{' '}
        <Link to="//src/Login.js">Register here</Link> {/* Use Link component */}
      </small>
    </div>
  );
};

export default Form;
