import React, { useState } from 'react';
import { addProduct } from './productService';


const ProductForm = () => {
  const [product, setProduct] = useState({ name: '', price: '', description: '' });

  const handleSubmit = (e) => {
    e.preventDefault();
    addProduct(product);
    setProduct({ name: '', price: '', description: '' });
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Add Product</h3>
      <input placeholder="Name" value={product.name} onChange={e => setProduct({ ...product, name: e.target.value })} />
      <input placeholder="Price" value={product.price} onChange={e => setProduct({ ...product, price: e.target.value })} />
      <textarea placeholder="Description" value={product.description} onChange={e => setProduct({ ...product, description: e.target.value })}></textarea>
      <button type="submit">Add Product</button>
    </form>
  );
};

export default ProductForm;
