import React from 'react';

const ProductListing = ({ products }) => {
  return (
    <div>
      <h3>Product List</h3>
      <div>
        {products.map(product => (
          <div key={product._id}>
            <h4>{product.name}</h4>
            <p>{product.description}</p>
            <p>Price: â‚¹{product.price}</p>
            {product.image && (
              <img
                src={`http://localhost:5000/uploads/${product.image}`}
                alt={product.name}
                style={{ width: '150px' }}
              />
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductListing;
