import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';
import { saveAs } from 'file-saver';
// import './App.css';
import './ProductPage.css'

function ProductPage() {
  const [productData, setProductData] = useState({
    name: '',
    image: null,
    price: '',
    description: '',
  });
  const [productList, setProductList] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProductData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleImageDrop = (acceptedFiles) => {
    setProductData((prev) => ({
      ...prev,
      image: acceptedFiles[0],
    }));
  };

  const { getRootProps, getInputProps } = useDropzone({
    accept: 'image/*',
    onDrop: handleImageDrop,
  });

  const handleAddProduct = () => {
    setProductList((prevList) => [...prevList, productData]);
    setProductData({ name: '', image: null, price: '', description: '' });
  };

  const handleExport = () => {
    const data = JSON.stringify(productList, null, 2);
    const blob = new Blob([data], { type: 'application/json' });
    saveAs(blob, 'products.json');
  };

  const handleImport = (e) => {
    const file = e.target.files[0];
    if (file && file.type === 'application/json') {
      const reader = new FileReader();
      reader.onload = () => {
        const importedData = JSON.parse(reader.result);
        setProductList(importedData);
      };
      reader.readAsText(file);
    } else {
      alert('Please upload a valid JSON file');
    }
  };

  return (
    <div className="admin-panel-container">
      <h2 className="admin-panel-title">Admin Panel</h2>

      <form className="product-form">
        <label className="form-label">
          Product Name:
          <input
            type="text"
            name="name"
            className="form-input"
            value={productData.name}
            onChange={handleChange}
          />
        </label>
        <label className="form-label">
          Price:
          <input
            type="number"
            name="price"
            className="form-input"
            value={productData.price}
            onChange={handleChange}
          />
        </label>
        <label className="form-label">
          Description:
          <textarea
            name="description"
            className="form-textarea"
            value={productData.description}
            onChange={handleChange}
          />
        </label>

        <div {...getRootProps()} className="image-upload-zone">
          <input {...getInputProps()} />
          <p className="image-upload-text">Drag & drop an image here, or click to select an image</p>
        </div>
        {productData.image && <p className="image-file-name">Image: {productData.image.name}</p>}

        <button type="button" className="add-product-button" onClick={handleAddProduct}>Add Product</button>
      </form>

      <div className="product-list">
        <h3 className="product-list-title">Product List</h3>
        <ul className="product-list-items">
          {productList.map((product, index) => (
            <li key={index} className="product-list-item">
              <strong className="product-name">{product.name}</strong><br />
              {product.image && <img className="product-image" src={URL.createObjectURL(product.image)} alt={product.name} />}
              <p className="product-description">{product.description}</p>
              <p className="product-price">Price: ${product.price}</p>
            </li>
          ))}
        </ul>

        <button className="export-button" onClick={handleExport}>Export Products</button>
        <div className="import-wrapper">
          <label className="import-button-label">
            Import Products (JSON)
            <input type="file" className="import-button" onChange={handleImport} accept=".json" />
          </label>
        </div>
      </div>
    </div>
  );
}

export default ProductPage;
