import React, { useState } from "react";
import './ProductPage.css'

// Sample product data
const products = [
  { id: 1, name: "Product 1", price: "$100", description: "Description of Product 1", image: "https://via.placeholder.com/150" },
  { id: 2, name: "Product 2", price: "$150", description: "Description of Product 2", image: "https://via.placeholder.com/150" },
  { id: 3, name: "Product 3", price: "$120", description: "Description of Product 3", image: "https://via.placeholder.com/150" },
  { id: 4, name: "Product 4", price: "$200", description: "Description of Product 4", image: "https://via.placeholder.com/150" },
  { id: 5, name: "Product 5", price: "$180", description: "Description of Product 5", image: "https://via.placeholder.com/150" },
  // Add more products here, up to 30...
];

function ProductPage() {
  const [selectedProduct, setSelectedProduct] = useState(null);

  const handleProductClick = (product) => {
    setSelectedProduct(product);
  };

  return (
    <div className="product-page">
      <div className="product-list">
        {products.map((product) => (
          <div
            key={product.id}
            className="product-card"
            onClick={() => handleProductClick(product)}
          >
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
          </div>
        ))}
      </div>

      {selectedProduct && (
        <div className="product-details">
          <img src={selectedProduct.image} alt={selectedProduct.name} />
          <h3>{selectedProduct.name}</h3>
          <p><strong>Price:</strong> {selectedProduct.price}</p>
          <p><strong>Description:</strong> {selectedProduct.description}</p>
        </div>
      )}
    </div>
  );
}

export default ProductPage;
