import React, { useEffect, useState } from 'react';
import { getProducts } from '../api/proudct';
import './ProductPage1.css'; // Make sure this path matches your file structure

const ProductPage1 = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const data = await getProducts();
      setProducts(data);
    } catch (err) {
      console.error(err);
      setError('Failed to load products.');
    }
  };

  const handleBuyNow = (product) => {
    alert(`You clicked "Buy Now" for "${product.name}"`);
    // Replace this with real checkout logic or navigation
  };

  return (
    <div className="product-main">
      <h2>Product List</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <div className="product-grid">
        {products.map((product) => (
          <div key={product._id} className="product-card">
            <h3>{product.name}</h3>
            <p><strong>Price:</strong> ${product.price}</p>
            <p>{product.description}</p>
            {product.image ? (
              <img src={`http://localhost:5000/uploads/${product.image}`} alt={product.name} width="50" />

            ) : (
              <p>No image</p>
            )}
            <button className="buy-button" onClick={() => handleBuyNow(product)}>
              Buy Now
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductPage1;
