import React, { useState, useEffect } from 'react';
import { addProduct, updateProduct, deleteProduct } from './productService';
import { jwtDecode } from 'jwt-decode'; // Corrected import for jwt-decode

const ProductForm = ({ editProduct, onSave }) => {
  const [product, setProduct] = useState({ name: '', price: '', description: '' });
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);
  const [successPopup, setSuccessPopup] = useState(false);

  useEffect(() => {
    if (editProduct) {
      setProduct({
        name: editProduct.name || '',
        price: editProduct.price || '',
        description: editProduct.description || '',
      });

      if (editProduct.image) {
        setPreview(`http://localhost:5000/uploads/${editProduct.image}`);
      }
    }
  }, [editProduct]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    setPreview(URL.createObjectURL(file));
  };

  // Token validation function
  const getToken = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token found');
      return null;
    }

    try {
      const decodedToken = jwtDecode(token); // Updated here to use jwtDecode
      if (decodedToken.exp * 1000 < Date.now()) {
        alert('Session expired. Please login again.');
        localStorage.removeItem('token'); // Remove expired token
        return null;
      }
      return token;
    } catch (error) {
      console.error('Invalid token:', error);
      localStorage.removeItem('token');
      return null;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const token = getToken();
    if (!token) return;

    const formData = new FormData();
    formData.append('name', product.name);
    formData.append('price', product.price);
    formData.append('description', product.description);
    if (image) formData.append('image', image);

    try {
      if (editProduct?._id) {
        await updateProduct(editProduct._id, formData, token);
      } else {
        await addProduct(formData, token);
      }

      setSuccessPopup(true);
      if (onSave) onSave();
      setProduct({ name: '', price: '', description: '' });
      setImage(null);
      setPreview(null);
    } catch (error) {
      console.error(error);
      alert('Error saving product: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleDelete = async () => {
    if (editProduct && window.confirm('Are you sure?')) {
      const token = getToken();
      if (!token) return;

      try {
        await deleteProduct(editProduct._id, token);
        if (onSave) onSave();
      } catch (error) {
        alert('Error deleting product');
        console.error(error);
      }
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <h3>{editProduct ? 'Edit' : 'Add'} Product</h3>
        <input placeholder="Name" value={product.name} onChange={e => setProduct({ ...product, name: e.target.value })} />
        <input placeholder="Price" value={product.price} onChange={e => setProduct({ ...product, price: e.target.value })} />
        <textarea placeholder="Description" value={product.description} onChange={e => setProduct({ ...product, description: e.target.value })} />
        <input type="file" accept="image/*" onChange={handleImageChange} />
        {preview && <img src={preview} alt="Preview" style={{ width: '150px' }} />}
        <button type="submit">{editProduct ? 'Update' : 'Add'} Product</button>
        {editProduct && <button type="button" onClick={handleDelete}>Delete</button>}
      </form>

      {successPopup && (
        <div style={popupStyle}>
          <div style={popupContentStyle}>
            <h4>Product successfully {editProduct ? 'updated' : 'added'}!</h4>
            <button onClick={() => setSuccessPopup(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

const popupStyle = {
  position: 'fixed',
  top: 0, left: 0,
  width: '100%', height: '100%',
  backgroundColor: 'rgba(0,0,0,0.5)',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
};

const popupContentStyle = {
  background: '#fff',
  padding: '20px',
  borderRadius: '8px',
  textAlign: 'center',
};

export default ProductForm;
