import React, { useState, useEffect } from 'react';
import { addProduct, updateProduct, deleteProduct } from './productService';

const ProductForm = ({ editProduct, onSave }) => {
  const [product, setProduct] = useState({ name: '', price: '', description: '' });
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);
  const [successPopup, setSuccessPopup] = useState(false);

  // When editProduct changes, pre-fill the form with the product data
  useEffect(() => {
    if (editProduct) {
      setProduct({
        name: editProduct.name || '',
        price: editProduct.price || '',
        description: editProduct.description || '',
      });

      if (editProduct.image) {
        setPreview(`http://localhost:5000/api/products/${editProduct.image}/image`);
      }
    }
  }, [editProduct]);

  // Handle image preview when the user selects an image
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    setPreview(URL.createObjectURL(file));
  };

  // Handle form submission (Add or Edit)
  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', product.name);
    formData.append('price', product.price);
    formData.append('description', product.description);
    if (image) {
      formData.append('image', image);
    }

    try {
      // Get the token from localStorage and log it for debugging
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No token found, please log in.');
      }

      // If we are editing, update the product; otherwise, add a new product
      if (editProduct?._id) {
        await updateProduct(editProduct._id, formData);
      } else {
        await addProduct(formData);
      }

      // Show success popup
      setSuccessPopup(true);
      if (onSave) onSave(); // Optionally refresh the product list

      // Reset form fields after success
      setProduct({ name: '', price: '', description: '' });
      setImage(null);
      setPreview(null);
    } catch (error) {
      console.error('Error saving product:', error);
      alert('Error saving product: ' + (error.response ? error.response.data.message : error.message));
    }
  };

  // Handle product deletion
  const handleDelete = async () => {
    if (editProduct && window.confirm('Are you sure you want to delete this product?')) {
      try {
        await deleteProduct(editProduct._id);
        if (onSave) onSave(); // Optionally refresh the product list
      } catch (error) {
        console.error('Error deleting product:', error);
        alert('Error deleting product: ' + (error.response ? error.response.data.message : error.message));
      }
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <h3>{editProduct ? 'Edit' : 'Add'} Product</h3>
        <input
          placeholder="Name"
          value={product.name}
          onChange={e => setProduct({ ...product, name: e.target.value })}
        />
        <input
          placeholder="Price"
          value={product.price}
          onChange={e => setProduct({ ...product, price: e.target.value })}
        />
        <textarea
          placeholder="Description"
          value={product.description}
          onChange={e => setProduct({ ...product, description: e.target.value })}
        />
        <input type="file" accept="image/*" onChange={handleImageChange} />
        {preview && <img src={preview} alt="Preview" style={{ width: '150px', marginTop: '10px' }} />}
        <button type="submit">{editProduct ? 'Update' : 'Add'} Product</button>
        {editProduct && <button type="button" onClick={handleDelete}>Delete Product</button>}
      </form>

      {/* Success Popup */}
      {successPopup && (
        <div style={popupStyle}>
          <div style={popupContentStyle}>
            <h4>Product successfully {editProduct ? 'updated' : 'added'}!</h4>
            <button onClick={() => setSuccessPopup(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

// Styles for success popup
const popupStyle = {
  position: 'fixed',
  top: 0,
  left: 0,
  width: '100%',
  height: '100%',
  backgroundColor: 'rgba(0, 0, 0, 0.5)',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
};

const popupContentStyle = {
  background: '#fff',
  padding: '20px',
  borderRadius: '8px',
  textAlign: 'center',
};

export default ProductForm;
