// App.js
import React, { useState } from "react";
// import ProductPage from "./ProductPage";
import ProductInfo from "./ProductInfo";
import AdminPanel from "./AdminPanel";
import productsData from "./productsData"; // Import the products data
import './ProductPage.css'

function ProductPage() {
  const [products, setProducts] = useState(productsData); // Initialize the products state from the imported data
  const [isAdmin, setIsAdmin] = useState(false);

  const handleProductUpdate = (updatedProduct) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.id === updatedProduct.id ? updatedProduct : product
      )
    );
  };

  return (
    <div className="App">
      <h1>Product Page</h1>
      <button onClick={() => setIsAdmin(!isAdmin)}>
        {isAdmin ? "Switch to User Mode" : "Switch to Admin Mode"}
      </button>

      {isAdmin ? (
        <AdminPanel products={products} onProductUpdate={handleProductUpdate} />
      ) : (
        <ProductInfo products={products} />

      )}
    </div>
  );
}

export default ProductPage;
