import React, { useState } from 'react';
import './Payment.css'

const PaymentModal = ({ onClose }) => {
  const [paymentMethod, setPaymentMethod] = useState('');
  const [upiID, setUpiID] = useState('');
  const [message, setMessage] = useState('');

  const handleUPIPayment = () => {
    if (!upiID) return setMessage('Please enter UPI ID');
    // Simulate UPI success
    setMessage(`Payment successful via UPI: ${upiID}`);
  };

  const handleCOD = () => {
    setMessage('Order placed. Pay ₹999 on delivery.');
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <h2>Select Payment Method</h2>
        <div className="payment-options">
          <button onClick={() => setPaymentMethod('upi')} className={paymentMethod === 'upi' ? 'active' : ''}>
            UPI
          </button>
          <button onClick={() => setPaymentMethod('cod')} className={paymentMethod === 'cod' ? 'active' : ''}>
            Cash on Delivery
          </button>
        </div>

        {paymentMethod === 'upi' && (
          <div className="upi-section">
            <input
              type="text"
              placeholder="Enter UPI ID"
              value={upiID}
              onChange={(e) => setUpiID(e.target.value)}
            />
            <button className="pay-button" onClick={handleUPIPayment}>
              Pay ₹999
            </button>
          </div>
        )}

        {paymentMethod === 'cod' && (
          <div className="cod-section">
            <button className="pay-button" onClick={handleCOD}>
              Confirm Order
            </button>
          </div>
        )}

        {message && <div className="message">{message}</div>}

        <button className="close-button" onClick={onClose}>
          Close
        </button>
      </div>
    </div>
  );
};

export default PaymentModal;
