import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api/products';

const authHeader = () => {
  const token = localStorage.getItem('token');
  if (!token) {
    throw new Error('No token found, please log in.');
  }
  return { Authorization: 'Bearer ' + token };
};

// Get all products
export const getProducts = async () => {
  try {
    const res = await axios.get(API_BASE_URL, { headers: authHeader() });
    return res.data;
  } catch (error) {
    console.error('Get products failed', error);
    throw error;
  }
};

// Add product
export const addProduct = async (formData) => {
  try {
    const res = await axios.post(API_BASE_URL, formData, {
      headers: {
        ...authHeader(),
        'Content-Type': 'multipart/form-data',
      },
    });
    return res.data;
  } catch (error) {
    console.error('Add product failed', error.response ? error.response.data : error.message);
    throw error;
  }
};

// Update product
export const updateProduct = async (id, formData) => {
  try {
    const res = await axios.put(`${API_BASE_URL}/${id}`, formData, {
      headers: {
        ...authHeader(),
        'Content-Type': 'multipart/form-data',
      },
    });
    return res.data;
  } catch (error) {
    console.error('Update product failed', error.response ? error.response.data : error.message);
    throw error;
  }
};

// Delete product
export const deleteProduct = async (id) => {
  try {
    const res = await axios.delete(`${API_BASE_URL}/${id}`, {
      headers: authHeader(),
    });
    return res.data;
  } catch (error) {
    console.error('Delete product failed', error.response ? error.response.data : error.message);
    throw error;
  }
};
