import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './deliverypage.css';

const DeliveryInfoPage = () => {
  const { state } = useLocation();
  const navigate = useNavigate();
  const product = state?.product;

  const [formData, setFormData] = useState({
    name: '',
    alternateNumber: '',
    address: '',
    pincode: '',
    paymentMode: 'COD', // Default to COD
    quantity: 1,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.paymentMode === 'Online') {
      handleOnlinePayment();
    } else {
      // For Cash on Delivery, just place the order
      alert('Order placed successfully with Cash on Delivery!');
      navigate('/');
    }
  };

  // Razorpay payment integration
  const handleOnlinePayment = () => {
    const options = {
      key: 'rzp_test_YourKeyHere', // Replace with your Razorpay API key
      amount: product.price * 100 * formData.quantity, // Amount in paise (multiply by quantity)
      currency: 'INR',
      name: 'My Store',
      description: `Payment for ${product.name}`,
      image: 'https://example.com/your-logo.png', // Add your store's logo URL
      handler: function (response) {
        alert(`Payment Successful! Payment ID: ${response.razorpay_payment_id}`);
        // Here you can save payment information to the backend if necessary
        navigate('/'); // Redirect to home or order success page
      },
      prefill: {
        name: formData.name,
        email: 'customer@example.com',
        contact: formData.alternateNumber,
      },
      theme: {
        color: '#3399cc',
      },
    };

    const rzp = new window.Razorpay(options);
    rzp.open();
  };

  if (!product) return <p className="no-product-message">No product selected. Go back to the product page.</p>;

  return (
    <div className="delivery-container">
      <h2 className="delivery-title">Delivery Information</h2>
      <div className="delivery-card">
        <div className="product-info">
          <img
            className="product-image"
            src={`http://localhost:5000/uploads/${product.image}`}
            alt={product.name}
          />
          <h3 className="product-name">{product.name}</h3>
          <p className="product-price">
            <strong>Price:</strong> ₹{product.price}
          </p>
        </div>
        <div className="form-info">
          <form onSubmit={handleSubmit} className="delivery-form">
            <div className="form-step">
              <label className="form-label">
                Full Name:
                <input
                  className="form-input"
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                />
              </label>
              <label className="form-label">
                Alternate Number:
                <input
                  className="form-input"
                  type="tel"
                  name="alternateNumber"
                  value={formData.alternateNumber}
                  onChange={handleChange}
                  required
                />
              </label>
              <label className="form-label">
                Address:
                <textarea
                  className="form-input"
                  name="address"
                  value={formData.address}
                  onChange={handleChange}
                  required
                />
              </label>
              <label className="form-label">
                Pincode:
                <input
                  className="form-input"
                  type="text"
                  name="pincode"
                  value={formData.pincode}
                  onChange={handleChange}
                  required
                />
              </label>
              <label className="form-label">
                Payment Mode:
                <select
                  className="form-input"
                  name="paymentMode"
                  value={formData.paymentMode}
                  onChange={handleChange}
                >
                  <option value="COD">Cash on Delivery</option>
                  <option value="Online">Online Payment</option>
                </select>
              </label>
              <label className="form-label">
                Quantity:
                <input
                  className="form-input"
                  type="number"
                  name="quantity"
                  value={formData.quantity}
                  onChange={handleChange}
                  min="1"
                  required
                />
              </label>
            </div>

            <div className="form-buttons">
              <button type="submit" className="place-order-button">
                {formData.paymentMode === 'Online' ? 'Proceed to Pay' : 'Place Order'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default DeliveryInfoPage;
