// ProductPage.js
import React, { useState } from "react";
import ProductCard from "./ProductCard";
import './ProductPage.css';

function ProductInfo({ products }) {
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [showPaymentOptions, setShowPaymentOptions] = useState(false);

  const handleProductClick = (product) => {
    setSelectedProduct(product);
    setShowPaymentOptions(false);
  };

  const handleBuyNow = () => {
    setShowPaymentOptions(true);
  };

  const handlePayment = (method) => {
    if (method === "Cash on Delivery") {
      alert(`Order placed for ${selectedProduct.name} with Cash on Delivery.`);
    } else if (method === "Online Payment") {
      loadRazorpay();
    }
    setShowPaymentOptions(false);
  };

  const loadRazorpay = () => {
    const options = {
      key: "rzp_test_YourTestKeyHere", // Replace with your Razorpay test key
      amount: selectedProduct.price * 100, // Amount in paise
      currency: "INR",
      name: "My Store",
      description: `Payment for ${selectedProduct.name}`,
      image: "https://yourdomain.com/logo.png", // Optional logo
      handler: function (response) {
        alert(`Payment successful! Payment ID: ${response.razorpay_payment_id}`);
      },
      prefill: {
        name: "John Doe",
        email: "john@example.com",
        contact: "9999999999"
      },
      notes: {
        product_id: selectedProduct.id,
      },
      theme: {
        color: "#3399cc"
      }
    };

    const rzp = new window.Razorpay(options);
    rzp.open();
  };

  return (
    <div className="product-page">
      <div className="product-list">
        {products.map((product) => (
          <ProductCard
            key={product.id}
            product={product}
            onClick={handleProductClick}
          />
        ))}
      </div>

      {selectedProduct && (
        <div className="product-details">
          <img src={selectedProduct.image} alt={selectedProduct.name} />
          <h3>{selectedProduct.name}</h3>
          <p><strong>Price:</strong> â‚¹{selectedProduct.price}</p>
          <p><strong>Description:</strong> {selectedProduct.description}</p>
          <button className="buy-now-btn" onClick={handleBuyNow}>Buy Now</button>

          {showPaymentOptions && (
            <div className="payment-options">
              <p>Select Payment Method:</p>
              <button onClick={() => handlePayment("Cash on Delivery")}>
                Cash on Delivery
              </button>
              <button onClick={() => handlePayment("Online Payment")}>
                Online Payment
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default ProductInfo;
