import React, { useState } from 'react';
import { addProduct, updateProduct } from './productService';

const ProductForm = ({ productToEdit }) => {
  const [product, setProduct] = useState(productToEdit || { name: '', price: '', description: '' });
  const [image, setImage] = useState(null);

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', product.name);
    formData.append('price', product.price);
    formData.append('description', product.description);
    if (image) {
      formData.append('image', image);
    }

    try {
      if (productToEdit) {
        // If editing a product, call updateProduct
        await updateProduct(productToEdit._id, formData);
      } else {
        // If adding a new product, call addProduct
        await addProduct(formData);
      }
      setProduct({ name: '', price: '', description: '' });
      setImage(null);
    } catch (error) {
      console.error('Error while submitting:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        value={product.name}
        onChange={(e) => setProduct({ ...product, name: e.target.value })}
        placeholder="Name"
      />
      <input
        type="number"
        value={product.price}
        onChange={(e) => setProduct({ ...product, price: e.target.value })}
        placeholder="Price"
      />
      <textarea
        value={product.description}
        onChange={(e) => setProduct({ ...product, description: e.target.value })}
        placeholder="Description"
      />
      <input type="file" onChange={handleImageChange} />
      <button type="submit">Submit</button>
    </form>
  );
};

export default ProductForm;
