// src/components/RegistrationForm.js
import React, { useState } from 'react';
import './Form.css';
import { Link, useNavigate } from 'react-router-dom';

const Form = () => {
  const [name, setName] = useState('');
  const [number, setNumber] = useState('');
  const [email, setEmail] = useState('');
  const [location, setLocation] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch('http://localhost:5000/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, number, email, location, password }),
      });

      const data = await res.json();

      if (res.status === 201) {
        alert('User Registered successfully!');
        navigate('/login'); // Redirect after successful registration
      } else if (res.status === 409) {
        alert('User is already registered. Redirecting to login.');
        navigate('/login');
      } else {
        alert(data.message || 'Something went wrong');
      }
    } catch (err) {
      console.error('Error:', err);
      alert('Server error. Please try again later.');
    }
  };

  return (
    
  <div class="container">
  <div class="form-box">
    <h1 id="form-title">Login</h1>

    <form id="login-form">
      <input type="email" placeholder="Email" required />
      <input type="password" placeholder="Password" required />
      <button type="submit">Login</button>
      <p class="switch-form">
        Don't have an account? <a href="#" onclick="switchForm('signup')">Sign up</a>
      </p>
    </form>

    <form id="signup-form" style="display: none;">
      <input type="text" placeholder="Full Name" required />
      <input type="email" placeholder="Email" required />
      <input type="password" placeholder="Password" required />
      <button type="submit">Sign up</button>
      <p class="switch-form">
        Already have an account? <a href="#" onclick="switchForm('login')">Login</a>
      </p>
    </form>
  </div>
</div>
  );
};

export default Form;
