// src/components/RegistrationForm.js
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

const Form = () => {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const [isUserRegistered, setIsUserRegistered] = useState(false); // Mock check if user exists
  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();

    // Mock check for existing user
    if (isUserRegistered) {
      // Redirect to login page if user is already registered
      history.push('/login');
    } else {
      // Here you'd add the logic to register the user (e.g., API call)
      alert('User Registered!');
    }
  };

  return (
    <div>
      <h2>Registration Form</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Register</button>
      </form>
      <small>
        Already have an account? <a href="/login">Login here</a>
      </small>
    </div>
  );
};

export default Form;
