import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaFacebook, FaInstagram, FaLinkedin } from 'react-icons/fa';
import MyImage from './image/logo1.png';
import './Nnavbar.css';

const NavBarHarsh = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const navigate = useNavigate();

  const isLoggedIn = !!localStorage.getItem('token');

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  const handleLoginRedirect = () => {
    navigate('/form'); // Use correct route here
  };

  return (
    <div>
      <div className="hero-section">
        <header className="header">
          {/* Top Bar */}
          <div className="top-bar">
            <div className="social-icons">
              <div className="social-media-icons">
                <a href="https://www.facebook.com" target="_blank" rel="noopener noreferrer">
                  <FaFacebook size={30} />
                </a>
                &nbsp;&nbsp;&nbsp;
                <a href="https://www.instagram.com" target="_blank" rel="noopener noreferrer">
                  <FaInstagram size={30} />
                </a>
                &nbsp;&nbsp;&nbsp;
                <a href="https://www.linkedin.com" target="_blank" rel="noopener noreferrer">
                  <FaLinkedin size={30} />
                </a>
              </div>
            </div>

            <div className="contact">
              <span>&nbsp;&nbsp;&nbsp;&nbsp;9752881492</span>
              <span>&nbsp;&nbsp;infozaeeshcorporation@gmail.com</span>
            </div>

            <div className="contact-1">
              {isLoggedIn ? (
                <button onClick={handleLogout} className="logout-btn">Logout</button>
              ) : (
                <button onClick={handleLoginRedirect} className="login-btn">Login | Sign-Up</button>
              )}
            </div>
          </div>

          {/* Navbar */}
          <nav className="navbar">
            <div className="logo--1">
              <img className='logo-img' src={MyImage} alt="Company Logo" />
            </div>
            <div className="menu-icon" onClick={() => setMenuOpen(!menuOpen)}>
              ☰
            </div>
            <div className='navmain--1'>
              <ul className={`nav-links ${menuOpen ? "show" : ""}`}>
                <li className="nav-item-home"><Link to="/">HOME</Link></li>
