import React, { useEffect, useState } from 'react';
import {
       getProducts,
     deleteProduct,
     updateProduct,
     createProduct,
    } from '../api/proudct';
    import DashboardMain from './DarshMain';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [editingProduct, setEditingProduct] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    price: '',
    description: '',
    image: ''
  });
  const [error, setError] = useState(null);
  const [isAdding, setIsAdding] = useState(false);

  const isLoggedIn = !!localStorage.getItem('token');

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const data = await getProducts();
      setProducts(data);
    } catch (err) {
      console.error('Error fetching products:', err);
      setError('Failed to load products: ' + err.message);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Delete this product?')) return;

    try {
      await deleteProduct(id);
      setProducts(products.filter((p) => p._id !== id));
    } catch (err) {
      console.error('Error deleting product:', err);
      setError('Failed to delete product.');
    }
  };

  const startEditing = (product) => {
    setEditingProduct(product._id);
    setFormData({
      name: product.name,
      price: product.price,
      description: product.description || '',
      image: product.image || ''
    });
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleUpdate = async (id) => {
    try {
      await updateProduct(id, formData);
      setEditingProduct(null);
      fetchProducts();
    } catch (err) {
      console.error('Error updating product:', err);
      setError('Failed to update product.');
    }
  };

  const handleAddProduct = async () => {
    try {
      await createProduct(formData); // Must exist in your API
      setIsAdding(false);
      setFormData({ name: '', price: '', description: '', image: '' });
      fetchProducts();
    } catch (err) {
      console.error('Error adding product:', err);
      setError('Failed to add product.');
    }
  };

  return (
    <>
    <DashboardMain></DashboardMain>
    <div>
      <h2>Product List</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* {isLoggedIn && !isAdding && (
        <button onClick={() => setIsAdding(true)}>Add Product</button>
      )} */}

      {isLoggedIn && isAdding && (
        <div style={{ marginBottom: '20px' }}>
          <h3>Add New Product</h3>
          <input name="name" placeholder="Name" value={formData.name} onChange={handleEditChange} />
          <input name="price" placeholder="Price" value={formData.price} onChange={handleEditChange} />
          <input name="description" placeholder="Description" value={formData.description} onChange={handleEditChange} />
          <input name="image" placeholder="Image filename" value={formData.image} onChange={handleEditChange} />
          <button onClick={handleAddProduct}>Submit</button>
          <button onClick={() => setIsAdding(false)}>Cancel</button>
        </div>
      )}

      <table border="1" cellPadding="10" style={{ borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>Name</th>
            <th>Price ($)</th>
            <th>Description</th>
            <th>Image</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) =>
            editingProduct === product._id ? (
              <tr key={product._id}>
                <td>
                  <input
                    name="name"
                    value={formData.name}
                    onChange={handleEditChange}
                  />
                </td>
                <td>
                  <input
                    name="price"
                    value={formData.price}
                    onChange={handleEditChange}
                  />
                </td>
                <td>
                  <input
                    name="description"
                    value={formData.description}
                    onChange={handleEditChange}
                  />
                </td>
                <td>
                  <input
                    name="image"
                    value={formData.image}
                    onChange={handleEditChange}
                  />
                </td>
                <td>
                  <button onClick={() => handleUpdate(product._id)}>Save</button>
                  <button onClick={() => setEditingProduct(null)}>Cancel</button>
                </td>
              </tr>
            ) : (
              <tr key={product._id}>
                <td>{product.name}</td>
                <td>{product.price}</td>
                <td>{product.description}</td>
                <td>
                  {product.image ? (
                    <img src={`http://localhost:5000/uploads/${product.image}`} alt={product.name} width="50" />
                  ) : (
                    'No image'
                  )}
                </td>
                <td>
                  {isLoggedIn && (
                    <>
                      <button onClick={() => startEditing(product)}>Edit</button>
                      <button onClick={() => handleDelete(product._id)}>Delete</button>
                    </>
                  )}
                </td>
              </tr>
            )
          )}
        </tbody>
      </table>
    </div>
    </>
  );
};

export default ProductList;
