import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './deliverypage.css';

const DeliveryInfoPage = () => {
  const { state } = useLocation();
  const navigate = useNavigate();
  const product = state?.product;

  const [formData, setFormData] = useState({
    name: '',
    alternateNumber: '',
    address: '',
    pincode: '',
    paymentMode: 'COD',
    quantity: 1,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Order Placed Successfully!\n' + JSON.stringify(formData, null, 2));
    navigate('/');
  };

  if (!product) return <p>No product selected. Go back to the product page.</p>;

  return (
    <div className="delivery-container">
      <h2>Delivery Information</h2>
      <div className="delivery-card">
        <img src={`http://localhost:5000/uploads/${product.image}`} alt={product.name} />
        <h3>{product.name}</h3>
        <p><strong>Price:</strong> ${product.price}</p>
        <form onSubmit={handleSubmit}>
          <div className="form-step">
            <label>
              Full Name:
              <input type="text" name="name" value={formData.name} onChange={handleChange} required />
            </label>
            <label>
              Alternate Number:
              <input type="tel" name="alternateNumber" value={formData.alternateNumber} onChange={handleChange} required />
            </label>
            <label>
              Address:
              <textarea name="address" value={formData.address} onChange={handleChange} required />
            </label>
            <label>
              Pincode:
              <input type="text" name="pincode" value={formData.pincode} onChange={handleChange} required />
            </label>
            <label>
              Payment Mode:
              <select name="paymentMode" value={formData.paymentMode} onChange={handleChange}>
                <option value="COD">Cash on Delivery</option>
                <option value="Online">Online Payment</option>
              </select>
            </label>
            <label>
              Quantity:
              <input type="number" name="quantity" value={formData.quantity} onChange={handleChange} min="1" required />
            </label>
          </div>

          <div className="form-buttons">
            <button type="submit">Place Order</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default DeliveryInfoPage;
