import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';  // Use the named import
import setAuthToken from '../utils/setAuthToken';  // Import the setAuthToken function

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/login', { email, password });

      // Store the token in localStorage
      localStorage.setItem('token', response.data.token);

      // Decode the token to get the role and other info
      const decodedToken = jwtDecode(response.data.token);
      localStorage.setItem('role', decodedToken.role);  // Store the role

      // Set the token in Axios headers
      setAuthToken(response.data.token);

      // Redirect based on role
      if (decodedToken.role === 'admin') {
        navigate('/admin-dashboard');
      } else {
        navigate('/user-dashboard');
      }
    } catch (err) {
      console.error('Login failed', err);
      alert('Login failed!');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
      />
      <input
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
      />
      <button type="submit">Login</button>
    </form>
  );
};

export default Login;
